'''
Find the greatest product of five consecutive digits in the 1000-digit number
'''
def consec(n):
	T = 0	#define current total
	temp = str(n)	#convert n to string
	for i in range(0, (len(temp))-5):	#loop from first letter to last - 5(amount of consecs)
		a = int(temp[i])
		b = int(temp[i+1])
		c = int(temp[i+2])
		d = int(temp[i+3])
		e = int(temp[i+4])
		t = a*b*c*d*e  #find total of current 5 numbers
		if t > T:	#if its bigger than current total, change it to current total
			T = t
			A = a
			B = b
			C = c 	#also store them in case you want them printed
			D = d
			E = e
	return print("The greatest product of five consecutive numbers in the series(" + str(A) + str(B) + str(C) + str(D) + str(E) + ") is " + str(T))

consec()
